module Day1Spec (main, spec) where

import Test.Hspec
import Test.Hspec.QuickCheck
import Test.QuickCheck
import Test.QuickCheck.Property
import Test.QuickCheck.Modifiers

import Test.Hspec

import Day1

main :: IO ()
main = hspec spec

spec :: Spec
spec = do

  describe "Subtracting parenthesis" $ do
    prop "A string with only closing parenthesis is the same as -length" $ 
      \(Positive x) -> (day1 $ replicate x ')') == -x

  describe "Adding parenthesis" $ do
    prop "A string with only open parenthesis is the same as the length" $ 
      \(Positive x) -> (day1 $ replicate x '(') == x

  describe "No matter the order returns the same" $ do
    it "returns the same" $ do
      day1 "(())" `shouldBe` day1 "()()"
      day1 "))(((((" `shouldBe` 3
      day1 ")())())" `shouldBe` -3


  describe "Finding position of basement" $ do
    it "returns 1 when is the first" $ do
      basementPos ")" `shouldBe` 1
    it "returns 5 when is the fifth" $ do
      basementPos "()())" `shouldBe` 5

  describe "Advent answer" $ do
